package com.java.aug;

public class IfElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int age=30;
		
		if(age<20){
			System.out.println("you are too young for the job");	
		}
		else if(age==30){
			System.out.println("you are apt for this job");
		}
		else if(age==40)
			System.out.println("we can do a test on you");
		
		else{
			System.out.println("you are not apt for this job");
		}
			
		
		
	}

}
